import Prismic from "@prismicio/client";
import dotenv from "dotenv";
dotenv.config();
const repository = 'https://shopify-product-variant-media.prismic.io/api/v2';
const key = 'MC5ZVDdCSlJBQUFDVUE0SVJq.AHgn77-977-9cGfvv71jHe-_vSzvv73vv71x77-9D1BH77-977-9KO-_ve-_vQ1O77-977-9N--_ve-_ve-_vQ';
export default function initApi(req) {
  return Prismic.getApi(repository, {
    accessToken: key,
    req: req
  });
}
