import Prismic from "@prismicio/client";
import dotenv from "dotenv";
dotenv.config();
const repository = ''
export default function initApi(req) {
  return Prismic.getApi(process.env.PRISMIC_REPOSITORY, {
    accessToken: process.env.PRISMIC_ACCESSTOKEN,
    req: req
  });
}
