import Prismic from "@prismicio/client";
import dotenv from "dotenv";
dotenv.config();
const repository = 'https://shopify-product-variant-media.prismic.io/api/v2';
export default function initApi(req) {
  return Prismic.getApi(repository, {
    accessToken: process.env.PRISMIC_ACCESSTOKEN,
    req: req
  });
}
